-- Function structure for FC_PRODUCT
-- ----------------------------
DROP FUNCTION IF EXISTS `FC_PRODUCT`;
delimiter ;;
CREATE FUNCTION `FC_PRODUCT`(`id` INT)
 RETURNS float
BEGIN
    DECLARE price float;
    DECLARE date_min DATETIME;
    
    SELECT MIN(pn.pn_ngaylapphieu) INTO date_min
    FROM chi_tiet_phieu_nhap ctpn 
    	LEFT JOIN phieu_nhap pn ON pn.pn_id = ctpn.ctpn_idpn
    WHERE ctpn.ctpn_idsp = id AND ctpn.ctpn_soluong > 0;
    

    RETURN price;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for insertHoaDon
-- ----------------------------
DROP PROCEDURE IF EXISTS `insertHoaDon`;
delimiter ;;
CREATE PROCEDURE `insertHoaDon`()
BEGIN

	DECLARE i INT DEFAULT 1; 
	DECLARE tenkh varchar(255);
	DECLARE shipper int;
	DECLARE tongtien float;
	DECLARE ngaytao DATE;
	WHILE (i <= 50) DO
					SET tenkh = (SELECT ten_kh
						FROM random_moi_thu
						ORDER BY rand()
						LIMIT 1);
					SET shipper = (SELECT shipper
						FROM random_moi_thu
						ORDER BY rand()
						LIMIT 1);
					SET ngaytao = (SELECT ngay_tao_hd
						FROM random_moi_thu
						ORDER BY rand()
						LIMIT 1);
						SET tongtien = (SELECT tongtien_hoadon
						FROM random_moi_thu
						ORDER BY rand()
						LIMIT 1);
			SET i = i+1;
	END WHILE;

END
;;
delimiter ;

