delimiter ;;
CREATE PROCEDURE `insertHoaDonNgayTao`()
BEGIN

	DECLARE i INT DEFAULT 1108; 
	DECLARE ngaytao DATE;
	WHILE (i <= 2107) DO
					SET ngaytao = (SELECT ngay_tao_hd
						FROM random_moi_thu
						ORDER BY rand()
						LIMIT 1);
						
			UPDATE  hoa_don SET hd_ngaytao = ngaytao where hd_id = i;
			SET i = i+1;
	END WHILE;

END
;;
