-- ----------------------------
-- Procedure structure for thongkeKhoangTG
-- ----------------------------
DROP PROCEDURE IF EXISTS `thongkeKhoangTG`;
delimiter ;;
CREATE PROCEDURE `thongkeKhoangTG`(startdate varchar(50), enddate varchar(50))
BEGIN 
	IF(startdate != '' AND enddate = '') THEN
		SELECT SUM(hd.hd_tongtien) FROM hoa_don hd LEFT JOIN trang_thai tt on hd.hd_id = tt.tt_idhd
				WHERE tt.tt_trangthai = 3 AND (DATE(hd_ngaytao) >= startdate);
				
		
 	ELSEIF (startdate = '' AND enddate !='') THEN
		SELECT SUM(hd.hd_tongtien) FROM hoa_don hd LEFT JOIN trang_thai tt on hd.hd_id = tt.tt_idhd
 				WHERE tt.tt_trangthai = 3 AND (DATE(hd_ngaytao) <= enddate);
 				
	ELSE 
	 SELECT SUM(hd.hd_tongtien) FROM hoa_don hd LEFT JOIN trang_thai tt on hd.hd_id = tt.tt_idhd
				WHERE tt.tt_trangthai = 3 AND (DATE(hd_ngaytao) BETWEEN startdate AND enddate);
				
 END IF;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for thongkeTheoThang
-- ----------------------------
DROP PROCEDURE IF EXISTS `thongkeTheoThang`;
delimiter ;;
CREATE PROCEDURE `thongkeTheoThang`(monthInput int, yearInput int)
BEGIN 
	SELECT SUM(hd.hd_tongtien) FROM hoa_don hd LEFT JOIN trang_thai tt on hd.hd_id = tt.tt_idhd
			where tt.tt_trangthai = 3 and MONTH(hd_ngaytao) = monthInput and YEAR(hd_ngaytao) = yearInput
			GROUP BY 
  CAST(YEAR(hd_ngaytao) AS VARCHAR(4)) + '-' + right('00' + CAST(MONTH(hd_ngaytao) AS VARCHAR(2)), 2);

END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table san_pham
-- ----------------------------
DROP TRIGGER IF EXISTS `insert_giaban`;
delimiter ;;
CREATE TRIGGER `insert_giaban` AFTER INSERT ON `san_pham` FOR EACH ROW BEGIN
	UPDATE gia_ban SET gb_gia = (SELECT DISTINCT c.ctpn_gia FROM chi_tiet_phieu_nhap c 
	LEFT JOIN san_pham s ON c.ctpn_idsp = s.sp_id LEFT JOIN phieu_nhap p on c.ctpn_idpn = p.pn_id,
		( SELECT MIN(pn.pn_ngaylapphieu) ngay_lap_phieu 
			FROM  chi_tiet_phieu_nhap ctpn LEFT JOIN phieu_nhap pn ON pn.pn_id = ctpn.ctpn_idpn
													GROUP BY  ctpn.ctpn_idsp) date_min
			WHERE s.sp_id = NEW.sp_id 
			and c.ctpn_soluong > 0 
			and p.pn_ngaylapphieu = date_min.ngay_lap_phieu) 
	WHERE gia_ban.gb_idsp = NEW.sp_id;
END
;;
delimiter ;

